import Vapor
import Fluent

final class NoteFieldModel: Model {
    
    static let schema: String = "note_fields"
    
    struct FieldKeys {
        static var label: FieldKey { "label" }
        static var value: FieldKey { "value" }
		static var noteId: FieldKey { "note_id" }
    }

    @ID() var id: UUID?
    @Field(key: FieldKeys.label) var label: String
    @Field(key: FieldKeys.value) var value: String
    @Parent(key: FieldKeys.noteId) var note: NoteModel

	init() { }
    
    init(id: UUID? = nil,
    	label: String, 
    	value: String, 
		noteId: UUID) 
	{
		self.id = id
    	self.label = label
    	self.value = value
		self.$note.id = noteId
	} 
}
